  const [cartIsShown , setCartIsShown] = useState(false);

  const showCartHandler = () => {
    setCartIsShown(true);
  }

  const hideCartHandler = () => {
    setCartIsShown
  }

  return (

    <Fragment>
      {cartIsShown && <Cart/>}
      <Header/>

      <main>
        <Meals/>
      </main>


    </Fragment>
  );
}

export default App;
